import { useState } from "react";
import moment from "moment";

function Logo() {
  return <h1>My Travel List</h1>;
}

function Form({ handleAddItem }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handleSubmit(e) {
    e.preventDefault();
    if (!description.trim()) {
      alert("Please enter a valid description.");
      return;
    }
    if (quantity <= 0) {
      alert("Please enter a positive quantity.");
      return;
    }

    const newItem = {
      id: Date.now(),
      description,
      quantity,
      packed: false,
      color: "#ffffff",
    };

    handleAddItem(newItem);
    setDescription("");
    setQuantity(1);
  }

  return (
    <form onSubmit={handleSubmit} className="add-form">
      <h3>What do you need to pack?</h3>
      <div>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(Number(e.target.value))}
          min="1"
        />
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Enter item..."
        />
        <button type="submit">Add</button>
      </div>
    </form>
  );
}

function Item({ item, handleDelete, handleTogglePacked, handleChangeColor, isRecentlyAdded }) {
  return (
    <li
      style={{
        textDecoration: item.packed ? "line-through" : "none",
        backgroundColor: isRecentlyAdded ? "#f0f9ff" : item.color,
      }}
    >
      <div>
        {item.description} ({item.quantity})
        <br />
        <small>{moment(item.id).format("MMM Do, h:mm a")}</small>
      </div>
      <div>
        <input
          type="color"
          value={item.color}
          onChange={(e) => handleChangeColor(item.id, e.target.value)}
        />
        <button onClick={() => handleTogglePacked(item.id)}>
          {item.packed ? "Unpack" : "Packed"}
        </button>
        <button onClick={() => handleDelete(item.id)}>Delete</button>
      </div>
    </li>
  );
}

function PackingList({ items, handleDelete, handleTogglePacked, handleChangeColor }) {
  const mostRecentlyAddedId = items.length > 0 ? Math.max(...items.map((item) => item.id)) : null;

  return (
    <div className="list">
      <ul>
        {items.map((item) => (
          <Item
            key={item.id}
            item={item}
            handleDelete={handleDelete}
            handleTogglePacked={handleTogglePacked}
            handleChangeColor={handleChangeColor}

          />
        ))}
      </ul>
    </div>
  );
}

function Stats({ items }) {
  const totalItems = items.length;
  const packedItems = items.filter((item) => item.packed).length;
  const packedPercentage = totalItems > 0 ? Math.round((packedItems / totalItems) * 100) : 0;

  return (
    <footer className="stats">
      <em>
        You have {totalItems} items in the list. You already packed {packedItems} (
        {packedPercentage}%).
      </em>
      <div className="progress-bar">
        <div
          style={{
            width: `${packedPercentage}%`,
          }}
        ></div>
      </div>
    </footer>
  );
}

function App() {
  const [items, setItems] = useState([]);

  function handleAddItem(item) {
    const updatedItems = [item, ...items];
    setItems(updatedItems);
  }

  function handleDelete(itemId) {
    const updatedItems = items.filter((item) => item.id !== itemId);
    setItems(updatedItems);
  }

  function handleTogglePacked(itemId) {
    const updatedItems = items.map((item) =>
      item.id === itemId ? { ...item, packed: !item.packed } : item
    );
    setItems(updatedItems);
  }

  function handleChangeColor(itemId, color) {
    const updatedItems = items.map((item) =>
      item.id === itemId ? { ...item, color } : item
    );
    setItems(updatedItems);
  }

  return (
    <div className="app">
      <Logo />
      <Form handleAddItem={handleAddItem} />
      <PackingList
        items={items}
        handleDelete={handleDelete}
        handleTogglePacked={handleTogglePacked}
        handleChangeColor={handleChangeColor}
      />
      <Stats items={items} />
    </div>
  );
}

export default App;
