import { useState } from "react";

// Initial packing items
const initialItems = [
  { id: 1, description: "Shirt", quantity: 5, packed: false },
  { id: 2, description: "Pants", quantity: 2, packed: true },
];

function Logo() {
  return <h1>My Travel List</h1>;
}



function Form() {
  const[description,setDescription]=useState("");
  const[quantity,setQuantity]=useState(1)
  const[items,setItems]=useState([]);

  function handleSubmit(e){
    e.preventDefault();
    const newItem={
      id:Date.now(),
      description,
      quantity,
      packed:false
    }
  }

  function handleAddItems(items){
  setItems((prevItems)=>[...prevItems,items])
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need to pack?</h3>
      <input
      type="text"
      value={description}
      onChange={(e)=>setDescription(e.target.value)}
      />
      <select
      name="values"
      id="values"
      value={quantity}
      onChange={(e)=>setQuantity(+e.target.value)}//plus converts the value into numbers
      >
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
      </select>
      <button type="submit">Add</button>
    </form>
  );
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} //item=item passes item as a prop for the map(item)
          />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  return (
    <li style={{ textDecoration: item.packed ? "line-through" : "none" }}>
      {item.description} ({item.quantity})
    </li>
  );

}

function Stats() {
  return (
    <footer className="stats">
      <em>You have X items in the list. You already packed Y (Z%).</em>
    </footer>
  );
}

function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}

export default App;
