import { useState } from "react";

// Initial packing items
const initialItems = [
  { id: 1, description: "Shirt", quantity: 5, packed: false },
  { id: 2, description: "Pants", quantity: 2, packed: true },
];

function Logo() {
  return <h1>My Travel List</h1>;
}

function handleUpdateItem(id){
  setItems((prevItems)=>
    prevItems.map((item)=>
    item.id===id?{...item,packed:!item.packed}:item
    )
  )
}

function Form({additem}) {
  const[description,setDescription]=useState("");
  const[quantity,setQuantity]=useState(1)

  function handleSubmit(e){
    e.preventDefault();
    if(!description) return;
    const newItem={
      id:Date.now(),
      description, //no need to pass anything as they are in the same scope ie "form"
      quantity, //essentially quantity:quantity/description:description
      packed:false,
    };
    //add new items to the field
    additem(newItem)

    //resets the states back to the default values
    setDescription("")
    setQuantity(1)
  }



  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need to pack?</h3>
      <input
      type="text"
      value={description}
      onChange={(e)=>setDescription(e.target.value)}
      />
      <select
      name="values"
      id="values"
      value={quantity}
      onChange={(e)=>setQuantity(+e.target.value)}//plus converts the value into numbers
      >
        <option value="1">1</option>
        <option value="2">2</option>
        <option value="3">3</option>
      </select>
      <button type="submit">Add</button>
    </form>
  );
}

function PackingList({items}) {
  return (
    <div className="list">
      <ul>
        {items.map((item) => (
          <Item item={item} key={item.id} //item=item passes item as a prop for the map(item)
          />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  return (
    <li style={{ textDecoration: item.packed ? "line-through" : "none" }}>
      {item.description} ({item.quantity})
    </li>
  );

}

function Stats() {
  return (
    <footer className="stats">
      <em>You have X items in the list. You already packed Y (Z%).</em>
    </footer>
  );
}

function App() {
  const[items,setItems]=useState([]);
  function handleAddItems(item){
    setItems((prevItems)=>[...prevItems,item])
  }

  return (
    <div className="app">
      <Logo />
      <Form 
      additem={handleAddItems}/>
      <PackingList items={items}/>
      <Stats />
    </div>
  );
}

export default App;
